mapper.select:
select tt.*
from (
       select *
       from user
       where data_level = 1
         and birthday = #{birthday}

        and id not in

          #{item}



         and sex = #{sex}

       order by id
     ) tt

select tt.*
from (
  select t.*
  from user t
  where t.data_level = 1
  and t.birthday != #{birthday}
  and (
    date_format(t.birthday, 'MM-dd') = date_format(#{birthday}, 'MM-dd')
      or
    date_format(t.birthday, 'yyyy') = date_format(#{birthday}, 'yyyy')
  )

    and t.id not in

      #{item}



    and sex = #{sex}

 order by birthday
) tt
