mapper.select:
select tt.*
from (
select *
from user
where data_level = 1
and sex = #{sex}
and birthday = #{birthday}

  and id not in

    #{item}


order by id
) tt

select tt.*
from (
select *
from user
where data_level = 1
and (
date_format(birthday, 'MM-dd') = date_format(#{birthday}, 'MM-dd')
or
to_days(birthday) between to_days(#{birthday}) - 365 and to_days(#{birthday}) + 365
)
and sex = #{sex}

  and id not in

    #{item}


) tt
